import { createSlice } from "@reduxjs/toolkit";

const msgSlice = createSlice({
    name:'msg',
    initialState:{
        loading:false
    },
    reducers:{
        msgSuccess(state,action){
            return{
                msg:action.payload
            }
        }
    }
})

const {actions,reducer} = msgSlice

export const {msgSuccess} = actions
export default reducer
