import React, { useEffect, useState } from 'react'
import './SildebarChat.css'
import { Avatar } from '@mui/material'
import axios from 'axios'

export default function SildebarChat({addNewChat,name,id}) {
   const [seed ,setSeed] = useState('')

   useEffect(()=>{
    setSeed(Math.floor(Math.random() * 5000))
   },[])

   const createChat = async()=>{
    const roomName = prompt("please enter your message!")
      if (roomName) {
        try {
          await axios.post('http://localhost:8000/api/v1/group/create',{
            groupName:roomName,
          })
        } catch (error) {
          console.log(error);
        }
      }
   }

   return !addNewChat ? (

   <div className='sildebarChat'>
         <Avatar
    src={`https://avatars.dicebear.com/api/human/${seed}.svg` }
    />
    <div className='sildebarChat_info'>
        <h2>AFlal</h2>
    </div>
   </div>

  ):(
    <div className='sildebarChat' onClick={createChat}>
        <h2>add new chat </h2>
    </div>
  )
}
