import React, { useEffect, useState } from 'react'
import './Chat.css'
import {Avatar, IconButton} from '@mui/material'
import {AttachFile, InsertEmoticon, MoreVert, SearchOutlined} from '@mui/icons-material'
import axios from 'axios'
import {useSelector} from 'react-redux'
import { useParams } from 'react-router-dom'

export default function Chat() {
  const [seed ,setSeed] = useState('')
  const [input , setInput] = useState('')
  const [roomName,setRoomName]= useState('')
  const [updateAt , setUpdateAt] = useState('')
  const [message,setmessage] = useState([])
  const {displayName,user} = useSelector(state=>state.authState)
  const {roomId} = useParams()

  const sendMessage = async(e)=>{
    e.preventDefault()
    console.log(input);

    if (!input) {
      return;
    }

    await axios.post(`http://localhost:8000/api/v1/message/new`,{
      name : displayName,
      message : input,
      timestamps : Date.now(),
      roomId : roomId,
      uid : user.uid
    })
    setInput('');
  }

  useEffect(()=>{
    axios.get(`http://localhost:8000/api/v1/room/${roomId}`).then((response)=>{
        setRoomName(response.data.data.name)
        setUpdateAt(response.data.data.updatedAt )
    })
    axios.get(`http://localhost:8000/api/v1/message/${roomId}`).then((response)=>{
        setmessage(response.data.data)
    })
  },[roomId])


   useEffect(()=>{
    setSeed(Math.floor(Math.random() * 5000))
   },[])
  return (
    <div className='chat'>
      <div className='chat_header'>
          <Avatar
        src={`https://avatars.dicebear.com/api/human/${seed}.svg` }
        />
      <div className='chat_headerInfo'>
          <h3> {roomName ? roomName : "welcome to whatsApp"} </h3>
          <p>{ updateAt ? `Last Update at ${new Date(updateAt).toString().slice(0,25)}` : "Click Any Groups"} </p>
      </div>

      <div className='chat_headerRight'>
          <IconButton>
            <SearchOutlined/>
          </IconButton>

          <IconButton>
            <AttachFile/>
          </IconButton>

          <IconButton>
            <MoreVert/>
          </IconButton> 
      </div>
    </div>
      <div className='chat_body'>
          <p className='chat_message'>
            <span className='chat_name'>Mohamed Aflal</span>
            Hello from Aflal
            <span className='chat_timestamp'>
              {new Date().toString().slice(0,25)}
            </span>
          </p>

          <p className='chat_message chat_receiver'>
            <span className='chat_name'>Mohamed Apsal</span>
            Hello from Apsal
            <span className='chat_timestamp'>
              {new Date().toString().slice(0,20)}
            </span>
          </p>
      </div>

      <div className='chat_footer'>
        <InsertEmoticon/>
        <form>
          <input
           placeholder='Type a message' 
           onChange={(e)=>setInput(e.target.value)}
           value={input}/>
          <button onClick={sendMessage}> send </button>
        </form>
      </div>

    </div>
  )
}
